import React, {Component, Fragment} from 'react';
import {Card, Col, Container, Row} from "react-bootstrap";
import {Link} from "react-router-dom";
import axios from "axios";
import ApiURL from "../../api/AppURL";


class FeaturedProducts extends Component {

    constructor() {
        super();
        this.state={
            ProductData:[]
        }
    }

    componentDidMount() {
        axios
            .get(ApiURL.ProductListByRemark("FEATURED"))
            .then((response) => {

                this.setState({ ProductData: response.data});

            })
            .catch((error) => {});
    }


    render() {
        return (
            <Fragment>
                <Container className="text-center" fluid={true}>
                    <h5 className="section-title">FEATURED PRODUCTS</h5>
                    <p className="section-sub-title">Some of Exclusive Collection, you May Like</p>
                    <Row>


                        <Col className="p-1" xl={2} lg={2} md={2} sm={4} xs={6}>

                            <Card className="image-box card w-100">
                                <img className="" src="/images/shoes 6.jpg" alt="shoes"/>
                                <Card.Body>
                                    <p className="product-name-on-card">PUMA Men's Alphacat Palmer Iced Tea Golf Shoe</p>
                                    <p className="product-price-on-card">Price: 3000TK</p>
                                </Card.Body>
                            </Card>
                        </Col>
                    </Row>
                </Container>
            </Fragment>
        );
    }
}

export default FeaturedProducts;
