import React, {Component, Fragment} from 'react';
import Slider from "react-slick";
import "slick-carousel/slick/slick-theme.css";
import "slick-carousel/slick/slick.css";

class SliderHome extends Component {
    render() {

        const settings = {
            dots: true,
            infinite: true,
            speed: 500,
            slidesToShow: 1,
            slidesToScroll: 1,
            autoPlay:true,
            autoplaySpeed:3000
        };

        return (
            <div className="slick-dots">

                <Slider {...settings}>
                    <div>
                        <img className="w-100 h-100" src="/images/cart_4.jpg" alt=""/>
                    </div>

                    <div>
                        <img className="w-100 h-100" src="/images/ba -1.jpg" alt=""/>
                    </div>

                </Slider>
            </div>
        );
    }
}

export default SliderHome;
