import React, {Component, Fragment} from 'react';
import {Container} from "react-bootstrap";

class MegaMenu extends Component {

    constructor(props) {
        super();
        this.state={
            data: props.MenuData
        }
    }



    MenuItemClick=(event)=>{
        event.target.classList.toggle('active');
        let panel = event.target.nextElementSibling;
        if (panel.style.maxHeight){
            panel.style.maxHeight = null;
        }else{
            panel.style.maxHeight = panel.scrollHeight+ "px";
        }
    }



    render() {

        let MyList = this.state.data;
       let MyView = MyList.map(MyList=>{
            return <Fragment>

                <button onClick={this.MenuItemClick} className="accordion"><img className="accordionMenuIcon" src="/images/boss.png"/>  Men's Clothing</button>

                <div className="panel">
                    <ul>
                        <li><a href="#" className="accordionItem">Man1 Shirt</a></li>
                        <li><a href="#" className="accordionItem">Man Shirt</a></li>

                    </ul>
                </div>

            </Fragment>
        })
        return (
            <Fragment>
                <Container fluid={true}>


            <div className="accordionMenuDiv">
                <div className="accordionMenuDivInside">

                    {MyView}

            </div>
            </div>
                </Container>
            </Fragment>
        );
    }
}

export default MegaMenu;
