import React, {Component, Fragment} from 'react';
import {Button, Card, Col, Container, Form, Row} from "react-bootstrap";
import {Link, Redirect} from "react-router-dom";

export default class Register extends Component {

    constructor() {
        super();
        this.state={
            Name:"",
            Email:"",
            Password:"",
            Confirm_Password:"",
            LoginRedirectStatus: false,
        }
        this.SearchChange = this.SearchChange.bind(this)
        this.SearchOnClick = this.SearchOnClick.bind(this)
        this.SearchRedirect = this.SearchRedirect.bind(this)
    }

    SearchChange(event){
      let SearchKey = event.target.value;
      this.setState({SearchKey:SearchKey});
    }

    SearchOnClick(){

        let SearchKey = this.state.SearchKey;
        if(SearchKey.length>=2){
            this.setState({SearchRedirectStatus:true})
        }
    }

    SearchRedirect(){
        if(this.state.SearchRedirectStatus===true){

            return <Redirect to={"/ProductListBySearch/"+this.state.SearchKey} />

        }
    }

  render() {
    return (
        <Fragment>
        <Container className="TopSection">
            <Row className="d-flex p-5  justify-content-center">
                <Col className="card mt-4" md={10} lg={10} sm={12} xs={12}>
                    <Row>
                        <Col className="text-center d-flex justify-content-center" md={6} lg={6} sm={12} xs={12}>
                            <Form className="onboardForm">
                            <h4 className="section-title">User Registration</h4>
                                <h6 className="section-sub-title pb-3"></h6>
                                <Form.Group className="mb-3" controlId="formBasicEmail">

                           
                                    <Form.Control className='mb-2' type="text" placeholder="Enter Your Name" />
                                    <Form.Control className='mb-2' type="text" placeholder="Enter Your Email" />
                                    <Form.Control className='mb-2' type="text" placeholder="Enter Your Password" />
                                    <Form.Control type="text" placeholder="Enter Your Confirm Password" />
                                    
                                </Form.Group>
                                <Form.Group>
                                <Button className="btn-block pure-material-button-contained site-btn me-2">Submit</Button>
                                <Link to="/onboard"  className="h4 btn">Login</Link>
                                </Form.Group>
                            </Form>
                        </Col>

                    <Col className="Desktop p-0 m-0" md={6} lg={6} sm={12} xs={12}>
                        <img className="onboardBanner w-75" src="/images/phone1.jpg"/>
                    </Col>
                    </Row>
                </Col>
            </Row>
        </Container>
   </Fragment>
    )
  }
}
