import React, { Component, Fragment } from "react";
import { Col, Container, Row } from "react-bootstrap";
import axios from "axios";
import ApiURL from "../../api/AppURL";
import ReactHtmlParser, {
  processNodes,
  convertNodeToElement,
  htmlparser2,
} from "react-html-parser";
class About extends Component {
  constructor() {
    super();
    this.state = {
      about: "",
    };
  }

  componentDidMount() {
    if (sessionStorage.getItem("SiteInfoAbout") == null) {
      axios
        .get(ApiURL.SendSiteInfo)
        .then((response) => {
          let StatusCode = response.status;
          if (StatusCode == 200) {
            let JSONDATA = response.data[0]["about"];
            this.setState({ about: JSONDATA });
            sessionStorage.setItem("SiteInfoAbout", JSONDATA);
          }
        })
        .catch((error) => {});
    } else {
      this.setState({ about: sessionStorage.getItem("SiteInfoAbout") });
    }
  }
  render() {
    return (
      <Fragment>
        <Container className="TopSection">
          <Row className="d-flex p-5  justify-content-center">
            <Col className="card mt-4" md={10} lg={10} sm={12} xs={12}>
              <div className="card-body">
                <div class="ph-item">
                  <div class="ph-col-12">
                    <div class="ph-picture"></div>
                    <div class="ph-row">
                      <div class="ph-col-12"></div>
                      <div class="ph-col-12"></div>
                      <div class="ph-col-12"></div>
                      <div class="ph-col-12"></div>
                      <div class="ph-col-12"></div>
                    </div>
                  </div>
                </div>
              </div>
            </Col>
            <Col className="card mt-4" md={10} lg={10} sm={12} xs={12}>
              <div className="card-body">
                {ReactHtmlParser(this.state.about)}
              </div>
            </Col>
          </Row>
        </Container>
      </Fragment>
    );
  }
}

export default About;
