import React, {Component, Fragment} from 'react';
import Slider from "react-slick";
import "slick-carousel/slick/slick-theme.css";
import "slick-carousel/slick/slick.css";
import {Container} from "react-bootstrap";

class SliderHome extends Component {
    render() {

        const settings = {
            dots: true,
            infinite: true,
            speed: 500,
            slidesToShow: 1,
            slidesToScroll: 1,
            autoPlay:true,
            autoplaySpeed:3000
        };

        const SliderData = this.props.data;
        const SliderView =  SliderData.map((SliderList, i)=>{

            return <div className="container-fluid bg-secondary overflow-hidden w-100 shadow-sm">
                <div className="row card-body">
                    <div className="col-md-6 slider-title-div text-center">
                        <h1 className="slider-title text-white">{SliderList.title}</h1>
                        <h1 className="slider-sub-title text-white">
                            {SliderList.sub_title}
                        </h1>
                        <button className="btn site-btn px-5">More Info</button>
                        <div className="col-md-6 text-center">
                            <img className="sliderImg" src= {SliderList.images} alt=""/>
                        </div>
                    </div>
                </div>

            </div>

        })


        return (
            <Fragment>
                <Container fluid={true} className="slider ">


            <div>

                <Slider  {...settings} >
                    <div>
                        <img className="slider-img" src="/images/cart_4.jpg" alt=""/>
                    </div>

                    <div>
                        <img className="slider-img" src="/images/ba -1.jpg" alt=""/>
                    </div>

                </Slider>
            </div>
                </Container>
            </Fragment>
        );
    }
}

export default SliderHome;
