import React, {Component, Fragment} from 'react';
import Slider from "react-slick";
import "slick-carousel/slick/slick-theme.css";
import "slick-carousel/slick/slick.css";
import {Container} from "react-bootstrap";

class SliderHome extends Component {
    render() {

        const settings = {
            dots: true,
            infinite: true,
            speed: 500,
            slidesToShow: 1,
            slidesToScroll: 1,
            autoPlay:true,
            autoplaySpeed:3000
        };

        const SliderData = this.props.data;
        const SliderView =  SliderData.map((SliderList, i)=>{

            return <div className="container-fluid bg-secondary overflow-hidden w-100 shadow-sm">
                <div className="row card-body">
                    <div className="col-md-6 slider-title-div text-center mt-5 pt-5">
                        <h1 className="slider-title text-white">{SliderList.title}</h1>
                        <h1 className="slider-sub-title text-white">
                            {SliderList.sub_title}
                        </h1>
                        <button className="btn site-btn px-5">More Info</button>
                    </div>
                        <div className="col-md-6 text-center">
                            <img className="sliderImg w-100 h-100 p-5" src= {SliderList.images} alt=""/>
                        </div>
                    X
                </div>

            </div>

        })


        return (






                <Slider  {...settings} >
                    {SliderView}

                </Slider>


        );
    }
}

export default SliderHome;
